class Solution {
public:
    int maxOperations(string s) {
        int left=0;
        int ans=0;
        int last=0;
        for(int i=0;i<s.size();++i){
            if (s[i]=='1'){
                if (last==0){
                    ans+=left;
                }
                left+=1;
                last=1;
            }
            else if (s[i]=='0'){
                last=0;
            }
        }
        if (last==0){
            ans+=left;
        }
        return ans;
    }
};
