class Solution {
    public int maxOperations(String s) {
        /**
        0123456
        1001101
           i
        j
        count=2

        find a substring that contains only 1
        get its first and last
        get the len 
        if there is a zero after the last
        then count+=len of operations

        accumulate the number of ones alone the way

        1001101
              i
        one= 4
        count=4
        */
        int count =0;
        int n =s.length();
        int i =0;
        int oneCount=0;
        while(i<n){
            if(s.charAt(i)=='1'){
                oneCount++;
                i++;
            }else{//cur is 0
                //update i to be  the next 1
                i++;
                while(i<n && s.charAt(i)=='0'){
                    i++;
                }
                count+=oneCount;
            }
        }
        return count;
    }
}
