class Solution {
public:
    int maxOperations(string s) {
        vector<int> v;
        for(int i = 0; i < s.size(); ++i)
        {
            if(s[i] == '1')
            {
                v.push_back(i);
            }
        }
        v.push_back(s.size());
        int total_cnt = 0;
        int curr_num = 1;

        // for(int num : v) cout << num << " ";
        // cout << "\n";
        for(int i = 0; i < v.size() - 1; ++i)
        {
            int diff = v[i+1] - v[i];
            if(abs(v[i] - v[i+1]) > 1)
            {
                total_cnt += curr_num;
                // cout << diff << " " << total_cnt << "\n";

            }
            curr_num++;
        }
        return total_cnt;
    }
};
