class Solution {
public:
    int maxOperations(string s) {
        long cur=0, ans=0;
        // for(char c : s){
        //     if(c=='1') cur++;
        // }
        if(s[0]=='1') cur=1;
        for(int i=1;i<s.size();i++){
            if(s[i]=='1') cur++;
            else if(s[i]=='0' && s[i-1]=='1')
                ans += cur;
            // cout<<i<<" "<<ans<<endl;
        }
        return ans;
    }
};
