class Solution {
public:
    int maxOperations(string s) {
        const int n = s.size();
        int ans = 0, cnt = 0, last = 0;
        for (int i = 0; i < n; ) {
            if (s[i] == '1') 
                ++cnt, ++i, last = i;
            else {
                while (s[i] == '0')
                    ++i;
                ans += cnt;
            } 
        }
        return ans;
    }
};
