class Solution:
    def maxOperations(self, s: str) -> int:
        s+="1"
        st=[]
        for i,c in enumerate(s):
            if c=="0":
                continue
            if len(st)==0 or i-st[-1][-1]>1:
                st.append([i])
            else:
                st[-1].append(i)
        res=0
        tot=0
        for x in st:
            res+=tot
            tot+=len(x)
        return res
