class Solution {
    public int maxOperations(String s) {
        int len = s.length();
        int count = 0;
        int ans = 0;
        boolean flag = false;
        for(int i = 0;i < len;i++){
            if(s.charAt(i) == '1'){
                if(flag){
                    ans += count;
                    flag = false;
                }
                count++;
            }else{
                flag = true;
            }
        }
        if(flag){
            ans += count;
        }
        return ans;
    }
}
