class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        a = [0] * n
        for i in range(n-1):
            if s[i] == '1':
                if s[i+1] == '0':
                    a[i] += 1
        ans = 0
        for i in range(n-2,-1,-1):
            a[i] += a[i+1]
        ans = 0
        for i in range(n-1):
            if s[i] == '1':
                ans += a[i]
        return ans
