class Solution {
public:
    int maxOperations(string s) {

        int ans=0;
        int tt=0;

        bool flg=true;

        int i = 0;
        while (i < s.size()) {
            if (s[i] == '1') {
                tt++;
                flg = true;
            } else {
                if (flg) {
                    ans += tt;
                }
                flg = false;
            }
            i++;
        }
        return ans;
    }
};
