class Solution {
public:
    int maxOperations(string s) {
        int a = 0, l = s.size(), c = 0;
        bool z = false;
        for(int i = 0; i < l; ++i){
            if(s[i] == '0'){
                z = true;
                continue;
            }
            if(z) a += c;
            ++c;
            z = false;
        }
        if(z){
            a += c;
        }
        return a;
    }
};
