class Solution {
public:
    int maxOperations(string s) {
        int res = 0;

        int n = s.size();

        int oneCnt = 0;
        bool shift = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                if (shift) {
                    res += oneCnt;
                }
                oneCnt ++;
                shift = 0;
            }
            else shift = 1;
        }
        if (shift){
            res += oneCnt;
        }
        return res;
    }
};
