class Solution {
public:
    int rec(int one_idx,vector<int> &ones,string &s)
    {
        if(one_idx == ones.size() or one_idx < 0)
            return 0;
        else
        {
            if(ones[one_idx] != ones.back())
            {
                if(ones[one_idx+1] - ones[one_idx] != 1)
                {
                    ones[one_idx] = ones[one_idx+1]-1;
                    if(one_idx == 0)
                        return (1 + rec(one_idx+1,ones,s));
                    else
                        return (1 + rec(one_idx-1,ones,s));
                }
                else
                {
                    return (rec(one_idx+1,ones,s));
                }
            }
            else
            {
                if(ones.back()!=s.length()-1)
                {
                    ones.back() = s.length()-1;
                    return (1 + rec(one_idx - 1,ones,s));
                }
                else
                    return (rec(one_idx+1,ones,s));
            }
        }
    }
    int maxOperations(string s) {
        int n  = s.length();
        vector<int> ones;
       for(int i = 0;i<n;i++)
       {
           if(s[i] == '1')
               ones.push_back(i);
       }
        int o =0,ans = 0;
        for(int i = 0;i<ones.size();i++)
        {
            o++;
            if(i != ones.size()-1)
            {
                if(ones[i+1] - ones[i]!=1)
                {
                    ans += o;
                }
            }
            else
            {
                if(ones[i] != n-1)
                    ans += o;
            }
        }
        return ans;

    }
};
