class Solution:
    def maxOperations(self, s: str) -> int:
        pre = s[0]
        c = 0
        li = []
        for el in s:
            if el == pre:
                c += 1
            else:
                li.append((pre, c))
                c = 1
            pre = el
        li.append((pre, c))
        #print(li)
        one_c = 0
        ans = 0
        for typ, fre in li:
            if typ == "0":
                ans += one_c
            else:
                one_c += fre
        return ans
