int maxOperations(char* s) {
    int res=0;
    int zerocnt=0,onecnt=0;
    for(int i=0;s[i]!='\0';i++){
        if(s[i]=='1') onecnt++;
        else{
            res+=onecnt;
            int j=i+1;
            while(s[j] != '\0' && s[j] == '0') j++;
            i=j-1;
        }
    }
    return res;
}
