class Solution {
public:
    int maxOperations(string s) {
        return solve(s);
    }

private:
    int solve(string s) {
        int ct = 0, res = 0;
        bool flag = false;
        int i = 0;
        int len = s.size();

        while (i < len) {
            if (s[i] == '0') {
                flag = true;
            }
            if (s[i] == '1') {
                if (flag) {
                    res += ct;
                }
                ct++;
                flag = false;
            }
            i++;
        }

        if (flag) {
            res += ct;
        }

        return res;
    }
};
