class Solution {
public:
    int maxOperations(string s) {
        const int n = s.length();
        int i = 0, ops = 0, ones = 0;

        while (i < n) {
            if (s[i] == '1') ones++;
            else {
                while (i < n - 1 && s[i + 1] == '0') i++;
                ops += ones;
            }
            i++;
        }

        return ops;
    }
};
