class Solution {
public:
    int maxOperations(string s) {
       int ans = 0;
        int cnt = 0;
        bool val = false;
        for(char c :s){
            if(c=='1'){
               cnt++;
                val = true;
            }else if(c=='0' && val){
                ans+=cnt;
                val = false;
            }
        }
        return ans;  


    }
};
