class Solution {
public:
    int maxOperations(string s) {
        int res = 0, n = s.size();
        int ones = 0;
        for (int i = 0; i < n - 1; i++) {
            if (s[i] == '1') {
                ones++;
                if (s[i+1] == '0') {
                    res += ones;
                }
            }
        }
        return res;
    }
};
