class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        p=0
        has_one=False
        bits=[]
        for char in s:
            if char=='1':
                bits.append(1)
                has_one=True
            elif char=='0' and has_one:
                bits.append(0)
                has_one=False
        for bit in bits:
            if bit==1:
                p+=1
            else:
                ans+=p
        return ans
