class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int one=0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='0'){
                ans+=one;
                while(s[i]=='0'&&i<s.length()){
                    i++;
                }
            }
            if(s[i]=='1'){
                one++;
            }

        }
        return ans;
    }
};
