class Solution {
    public int maxOperations(String s) {
        int ans = 0, cnt = 0;
        var zero = false;
        for (var c : s.toCharArray()) {
            if (c == '0') {
                zero = true;
            } else {
                if (zero) {
                    ans += cnt;
                    zero = false;
                }
                cnt++;
            }
        }
        if (zero) {
            ans += cnt;
        }
        return ans;
    }
}
