class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int countOne=0;
        int i=0;
        while(i<s.size())
        {
            if(s[i]=='1')
            {
                countOne++;
                i++;
            }
            else
            {
                int countZero=0;
                while(i<s.size() && s[i]!='1')
                {
                    countZero++;
                    i++;
                }
                if(countZero)
                {
                    ans+=countOne;
                }
            }
        }
        return ans;
    }
};
