using namespace std;
using LL = long long;
const int INF = 0x3f3f3f3f;
const LL LINF = 0x3f3f3f3f3f3f3f3f;

class Solution
{
public:
    int maxOperations(string s)
    {
        int n = s.size();
        int ans = 0;
        int now = 0;
        bool prev = true;
        for (int i = 0; i < n; i++)
        {
            if (s[i] == '1')
            {
                if (not prev)
                {
                    ans += now;
                    prev = true;
                }
                now++;
            }
            else
            {
                prev = false;
            }
        }
        if (not prev)
            ans += now;
        return ans;
    }
};
