class Solution {
    public int maxOperations(String s) {
        int n = s.length(), curr = 0, ans = 0;
        char[] c = s.toCharArray();
        for(int i=n-1; i>=0; i--){
            if(i>0 && c[i]=='0' && c[i-1]=='1') curr++;
            if(c[i]=='1') ans += curr;
        }
        return ans;
    }
}
