class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        c1 = 0
        c0 = 0
        for i, n in enumerate(s):
            if n == '0':
                c0 += 1
            else:
                if c0 > 0:
                    ans += c1
                c1 += 1
                c0 = 0
        return ans + (c1 if c0 > 0 else 0)
