class Solution {
public:
    int maxOperations(string s) {
        int c1 = 0;
        bool check = false;
          int ans = 0;


        for (char ch : s) {
            if (ch == '1') {
                c1++;  
                check = true;
            } else if (ch == '0' && check) {
                ans += c1;  
                check = false;    
            }
        }

        return ans;
    }
};
