class Solution {
public:
    int maxOperations(string s) {
        //remove adjacent 0s, 
        string new_string = ""; 
        for(auto c: s){
            if(c == '0'){
                if(new_string.size() == 0 || new_string.back() != '0'){
                    new_string+='0';
                }
            }else{
                new_string+='1';
            }
        }
        //cout<<"removed adj 0s : "<<new_string<<endl;
        s = new_string;  

        int ans = 0; 
        int one_count_to_left = 0; 
        for(auto c: s){
            if(c == '0'){
                //cout<<"current one_count_to_left : "<<one_count_to_left<<endl; 
                //cout<<"current ans : "<<ans<<endl; 
                ans+=one_count_to_left;
            }else{
                one_count_to_left++; 
            }
        }
        return ans; 

    }
};
