class Solution {
    public int maxOperations(String s) {
        int count = 0,output = 0;
        for(int i=0;i<s.length();i++){
            boolean check = (s.charAt(i) == '0');
            if(s.charAt(i) == '1') count++;
            while(i<s.length() && s.charAt(i) == '0') i++;
            if(check) { output += count; i--; }
        }
        return output;
    }
}
