class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt=0;
        for(int i=s.length()-1;i>=0;i--)
        {
            if((s[i]=='0'&&i==s.length()-1)||(s[i]=='0'&&s[i+1]!='0'))
            cnt++;

            if(s[i]=='1')
            ans+=cnt;
        }
        return ans;
    }
};
