class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int t=0;
        string u="";
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                u+='1';
            }
            else{
                if(i-1>=0&&s[i-1]=='1'){
                    u+='0';
                }
            }
        }
        //std::cout<<u;
        for(int i=0;i<u.size();i++){
            if(u[i]=='1'){
                t++;
            }
            else{
                res+=t;
            }
        }
        return res;
    }

};
