class Solution {
    public int maxOperations(String s) {
        int ops = 0;
        int cnt = 0;
        boolean seen = false;
        int i = 0;
        int len = s.length();

        List<Integer> segs = new ArrayList<>();

        while (i < len) {
            char c = s.charAt(i);
            if (c == '1') {
                segs.add(1);
                seen = true;
            } else if (c == '0' && seen) {
                segs.add(0);
                seen = false;
            }
            i++;
        }

        i = 0;
        int sz = segs.size();
        while (i < sz) {
            if (segs.get(i) == 1) {
                cnt++;
            } else {
                ops += cnt;
            }
            i++;
        }

        return ops;
    }
}
