class Solution {
public:
    int maxOperations(string s) {
        int ans=0,n=s.size(),c=0,c1=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                if(c==1){
                    ans+=c1; c=0;
                }
                c1++;
            }
            if(s[i]=='0')c=1;
        }
        if(c==1){
            ans+=c1;
        }
        return ans;
    }
};
