class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int prev_one = 0; 
        int op_cnt = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1') {
                prev_one += (i < (n - 1) && s[i + 1] == '0');
                op_cnt += prev_one;
            }
        }
        return op_cnt;
    }
};
