/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
let ans = 0, pre = -1, c = 0
for (let i = 0; i < s.length; i += 1) {
    if (s[i] === '1') {
        if (pre + 1 !== i) ans += c
        pre = i
        c += 1
    }
}
if (s[s.length - 1] !== '1') ans += c
return ans
};
