class Solution {
#define pb push_back
public:
    int maxOperations(string s) {
        int ans = 0;
        int pre = 0;
        bool flag = false;
        vector<int>temp;
        for(char ch:s){
            if(ch=='1'){
                temp.pb(1);
                flag = true;
            }
            else if(ch=='0' && flag){
                temp.pb(0);
                flag = false;
            }
        }
        for (int i : temp){
            if(i==1)    pre++;
            else{
                ans+=pre;
            }
        }
        return ans;
    }
};
