class Solution {
public:
    int maxOperations(string s) {
        int ans=0, cnt=0;
        for (int i=0; i<s.size(); i++) {
            if (s[i]=='1') cnt+=1;
            else {
                if (i+1<s.size()&&s[i]==s[i+1]) continue;
                ans+=cnt;
            }
        }
        return ans;
    }
};
