class Solution {
public:
    int maxOperations(string s) {
        int oc=0,ans=0;
        int f=0;
        for(char c:s) {
            if(c=='0'){
                f=1;
            }else{
                if(f)ans+=oc; 
                f=0;
                oc++;
            }
        }
        if(f)ans+=oc;
        return ans; 
    }
};
