class Solution {
public:
    int maxOperations(string s) {
        if(s.back() == '0') s.push_back('1');
        int ans = 0, tmp = 0;
        int n = s.size();
        for(int i = 0, j; i < n; i = j) {
            j = i;
            while(j < n && s[i] == s[j]) j++;
            int len = j - i;
            if(s[i] == '1') {
                ans += tmp;
                tmp += len;
            }
        }
        return ans;
    }
};
