class Solution {
public:
    int maxOperations(string s) {
        int ones =0, res=0;
        bool prevZero = false;
        for(int i=0; i<s.length(); i++){
            if(s[i]=='1'){
                if(prevZero){
                    res+=ones;
                    prevZero = false;
                }
                ones++;
            }
            else prevZero = true;
        }
        if(prevZero) res+=ones;
        return res;
    }
};
