class Solution:
    def maxOperations(self, s: str) -> int:
        segs = [seg for seg in s.split('0') if seg]
        cnts = [len(seg) for seg in segs]

        if s[-1] == '1':
            ops = 0
            cum_cnt = 0
            for cnt in cnts[:-1]:
                ops += cnt + cum_cnt
                cum_cnt += cnt
        else:
            ops = 0
            cum_cnt = 0
            for cnt in cnts:
                ops += cnt + cum_cnt
                cum_cnt += cnt

        return ops
