class Solution {
public:
    int maxOperations(string s) {
        string k="";
        int n= s.length();
        for(int i=0; i<n; i++){
            if(s[i]=='0'){
                while(i+1<n && s[i+1]==s[i]) i++;
            }
            k+=s[i];
        }
        // cout<<k<<" ";
        n=k.length();
        int res=0;
        for(int i=0, ones=0; i<n; i++){
            if(k[i]=='1') ones++;
            else res+=ones;
        }
        return res;
    }
};
