class Solution {
public:
    int maxOperations(string s) {
        int res=0;
        int curCount=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                continue;
            }
            if(i&&s[i-1]=='0'){
                res+=curCount;
            }
            ++curCount;
        }
        if(s[s.size()-1]=='0'){
            res+=curCount;
        }
        return res;
    }
};
