class Solution {
    public int maxOperations(String s) {
        int ans = 0, count = 0;
        if (s.charAt(s.length() - 1) == '0') count++;
        for (int i = s.length() - 1; i >= 0; i--) {
            char now = s.charAt(i);
            if (now == '1') {
                ans += count;
            }
            if (i != s.length() - 1 && now == '0' && s.charAt(i + 1) == '1') {
                count++;
            }
        }
        return ans;
    }
}
