class Solution {
public:
    int maxOperations(string s) {
        while (!s.empty() && s.back() == '1') s.pop_back();
        int n = int(s.length());


        int cnt = 0;
        int ans = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1') {
                if (s[i + 1] != '1') cnt++;
                ans += cnt;
            }
        }
        return ans;
    }
};
