class Solution:
    def maxOperations(self, s: str) -> int:
        prev = 0
        n = len(s)
        i = 0
        ans = 0

        zero = False
        while i < n:
            if s[i] == '1':
                if zero:
                    ans += prev

                while i < n and s[i] == '1':
                    prev += 1
                    i += 1

            else:
                while i < n and s[i] == '0':
                    zero = True
                    i += 1

        if s[-1] == '0':
            ans += prev

        return ans
