class Solution {
public:
    int maxOperations(string s) {
      int ans=0,onn=0;
        bool flag=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                onn++;
                flag=true;
            }
            else{
                if(flag)
                ans += onn;
                flag=false;
            }
        }
        return ans;  
    }
};
