class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int i = 0;
        int ones = 0;

        while(i < s.length()){
            while(i < s.length() && s.charAt(i) == '0'){
                i++;
            }
            while(i < s.length() && s.charAt(i) == '1'){
                i++;
                ones++;
            }
            if(i == s.length()){
                return count;
            }
            else{
                count += ones;
            }
        }
        return count;
    }
}
