class Solution {
public:
    int maxOperations(string s) {
        int end = s.size()-1;
        while(end >= 0) {
            if(s[end] == '0') {
                break;
            }
            end--;
        }

        // ok we can move till end;
        int count = 0;
        int ret = 0;
        for(int i=0;i<end;) {
            if(s[i] == '0') {
                i++;
                continue;
            }
            while(i<end && s[i] == '1') {
                // count continous 1s;
                count++;
                i++;
            }
            ret += count;
        }
        return ret;
    }
};
