class Solution {
public:
    int maxOperations(string s) {
        int last0 = -1;
        for (int i = s.size() - 1; i >= 0; i--) {
            if (s[i] == '0') {
                last0 = i;
                break;
            }
        }
        if (last0 == -1 || last0 == 0) return 0;
        typedef long long LL;
        vector<LL> sum;
        sum.emplace_back(0);
        LL pre = 0;
        int num = 0;//连续区间1的数量
        s += '1';
        bool vis = false;
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1' && vis) {
                sum.emplace_back(pre + num);
                pre += num;
                vis = false;
                num = 0;
            }
            if (s[i] == '1') num++;
            else
                vis = true;
        }
        LL ans = 0;
        for (auto e : sum)
            ans += e;
        return ans;
    }
};
