class Solution {
public:
    int maxOperations(string s) {
        int one = 0, ans = 0, n = s.size();
        // string s = "";
        // for(int i=0 ; i<n ; i++){
        //     if(str[i] == '1')   s += '1';
        //     while()
        // }
        for(int i=0 ; i<n ; i++){
            if(s[i] == '1')    one++;
            if(s[i] == '0'){
                ans += one;
                while(i < n && s[i] == '0')   i++;
                i--;
            }
            // cout << ans << " " << one << endl;
        }
        return ans;
    }
};
